mapper.select:
select sum(a.BALANCE) BALANCE, '00' as CARD_ID, '合计' as CARD_NAME, '-' as CARD_NUM, '-' as CARD_OWNER, '-' as CARD_BANK, '-' as REMARK, '-' as CARD_STATE from ( select (select BALANCE from CARD_DETAIL where DETAIL_ID = (select max(DETAIL_ID) from CARD_DETAIL where CARD_ID = c.CARD_ID) ) BALANCE from CARD_INFO c where 1=1
and c.CARD_NAME like CONCAT('%',#{CARD_NAME},'%')
and c.CARD_NUM like CONCAT('%',#{CARD_NUM},'%')
and c.CARD_STATE = #{CARD_STATE}
and c.CARD_STATE = '00'
) a

mapper.update:
update CARD_INFO
set CARD_STATE = #{CARD_STATE}
where CARD_ID = #{CARD_ID}

mapper.select:
select DETAIL_ID, DETAIL_NUM, DETAIL_DATE, DETAIL_EXPLAIN, AMOUNT, PAY_TYPE, BALANCE, PAY_CLASS, PAY_EXPLAIN, BILL_NUM, REMARK, CARD_ID from CARD_DETAIL where CARD_ID = #{CARD_ID}
and DETAIL_DATE >= #{START_TIME}
and DETAIL_DATE <= #{END_TIME}
and PAY_CLASS = #{PAY_CLASS}
and PAY_TYPE = #{PAY_TYPE}
order by DETAIL_NUM desc

select
'00' as DETAIL_ID,
'-' as DETAIL_NUM,
'合计' as DETAIL_DATE,
'-' as DETAIL_EXPLAIN,
(select sum(REAL_AMOUNT) from CARD_DETAIL 
	where CARD_ID = #{CARD_ID}

	and DETAIL_DATE >= #{START_TIME}


	and DETAIL_DATE <= #{END_TIME}


	and PAY_CLASS = #{PAY_CLASS}


	and PAY_TYPE = #{PAY_TYPE}
) as AMOUNT,
'-' as PAY_TYPE,
'-' as BALANCE,
'-' as PAY_CLASS,
'-' as PAY_EXPLAIN,
'-' as BILL_NUM,
'-' as REMARK,
'-' as CARD_ID
from dual

select '工资' as NAME,
(select sum(AMOUNT) from CARD_DETAIL where CARD_ID = #{CARD_ID} and PAY_TYPE = '00' and PAY_CLASS = '00'
	
		and DETAIL_DATE >= #{START_TIME}
	
	
		and DETAIL_DATE <= #{END_TIME}
	) as INCOME,
(select sum(AMOUNT) from CARD_DETAIL where CARD_ID = #{CARD_ID} and PAY_TYPE = '01' and PAY_CLASS = '00'
	
		and DETAIL_DATE >= #{START_TIME}
	
	
		and DETAIL_DATE <= #{END_TIME}
	) as EXPEND,
(select sum(REAL_AMOUNT) from CARD_DETAIL where CARD_ID = #{CARD_ID} and PAY_CLASS = '00'
	
		and DETAIL_DATE >= #{START_TIME}
	
	
		and DETAIL_DATE <= #{END_TIME}
	) as BALANCE
from dual
