mapper.select:
select '-' DETAIL_ID, '-' DETAIL_NUM, '合计' DETAIL_DATE, '-' DETAIL_EXPLAIN, sum(a.AMOUNT) AMOUNT, '-' PAY_TYPE, '-' BALANCE, '-' PAY_CLASS, '-' PAY_EXPLAIN, '-' BILL_NUM, '-' REMARK, '-' CARD_ID, '-' REAL_AMOUNT, '-' CARD_NAME, '-' CARD_NUM, '-' CARD_OWNER, '-' CARD_BANK, '-' CARD_STATE, '-' PROJECT_NAME, '-' PROJECT_TYPE, sum(a.RETURN_AMOUNT_SUM) RETURN_AMOUNT_SUM from ( select cd.AMOUNT, (select sum(dr.RETURN_AMOUNT) from deposit_return dr where dr.DETAIL_ID = cd.DETAIL_ID ) RETURN_AMOUNT_SUM from card_detail cd left join deposit_manage dm on dm.DETAIL_ID = cd.DETAIL_ID left join card_info ci on ci.CARD_ID = dm.CARD_ID left join project_info pro on pro.PROJECT_ID = dm.PROJECT_ID where cd.PAY_TYPE = '01' and cd.PAY_CLASS = '02'
and cd.DETAIL_NUM like CONCAT('%',#{DETAIL_NUM},'%')
and pro.PROJECT_NAME like CONCAT('%',#{PROJECT_NAME},'%')
and pro.PROJECT_TYPE = #{PROJECT_TYPE}
and pro.PROJECT_STATE = #{PROJECT_STATE}
and ci.CARD_NAME like CONCAT('%',#{CARD_NAME},'%')
and ci.CARD_NUM like CONCAT('%',#{CARD_NUM},'%')
) a

mapper.update:
update DEPOSIT_MANAGE set 
PROJECT_ID = #{PROJECT_ID},
CARD_ID = #{CARD_ID}
where DETAIL_ID = #{DETAIL_ID}

mapper.select:
select RETURN_ID, RETURN_DATE, RETURN_AMOUNT, RETURN_REMARK, DETAIL_ID from deposit_return where DETAIL_ID = #{DETAIL_ID}
and RETURN_DATE >= #{RETURN_START_TIME}
and RETURN_DATE <= #{RETURN_END_TIME}
order by RETURN_ID desc

select
'00' as RETURN_ID,
'合计' as RETURN_DATE,
(select sum(RETURN_AMOUNT) from deposit_return 
	where DETAIL_ID = #{DETAIL_ID}

	and RETURN_DATE >= #{RETURN_START_TIME}


	and RETURN_DATE <= #{RETURN_END_TIME}
) as RETURN_AMOUNT,
'-' as RETURN_REMARK,
'-' as DETAIL_ID
from dual

mapper.update:
update DEPOSIT_RETURN
set RETURN_DATE = #{RETURN_DATE},
	RETURN_AMOUNT = ${RETURN_AMOUNT},
	RETURN_REMARK = #{RETURN_REMARK}
where RETURN_ID = #{RETURN_ID}

mapper.select:
select DETAIL_ID 
from deposit_manage
where  DETAIL_ID = #{DETAIL_ID}
