mapper.update:
update first_payment set 
PAYDATE = #{PAYDATE},
PAYMENT = ${PAYMENT},
BALANCE = ${BALANCE},
REMARK = #{REMARK}
where FIRST_ID = #{FIRST_ID}

mapper.select:
select ii.INPUT_ID, ii.INPUT_NUM, ii.PROJECT_ID, ii.PAYDATE, ii.PAYMENT, ii.PAYNOTAX, ii.TAXVALUE, ii.TAXBALANCE, ii.STATE, ii.TYPE, ii.COMPANY, ii.REMARK, ii.INVOICE_TYPE, (select sum(ip.IP_AMOUNT) from invoice_pay ip where ip.input_id = ii.INPUT_ID) PAID from input_invoice ii where PROJECT_ID = #{PROJECT_ID}
and ii.STATE = #{STATE}
and ii.TYPE = #{TYPE}
and ii.INVOICE_TYPE = #{INVOICE_TYPE}
union all select '-' INPUT_ID, '-' INPUT_NUM, '00' PROJECT_ID, '合计' PAYDATE, sum(ii.PAYMENT) PAYMENT, sum(ii.PAYNOTAX) PAYNOTAX, sum(ii.TAXVALUE) TAXVALUE, sum(ii.TAXBALANCE) TAXBALANCE, '-' STATE, '-' TYPE, '-' COMPANY, '-' REMARK, '-' INVOICE_TYPE, (select sum(ip.IP_AMOUNT) from invoice_pay ip where ip.input_id in (select input_id from input_invoice ii2 where ii2.PROJECT_ID = #{PROJECT_ID}
and ii2.STATE = #{STATE}
and ii2.TYPE = #{TYPE}
and ii2.INVOICE_TYPE = #{INVOICE_TYPE}
) ) PAID from input_invoice ii where ii.PROJECT_ID = #{PROJECT_ID}
and ii.STATE = #{STATE}
and ii.TYPE = #{TYPE}
and ii.INVOICE_TYPE = #{INVOICE_TYPE}
order by PAYDATE asc

select IP_ID, IP_NUM, IP_DATE, IP_AMOUNT, IP_REMARK, INPUT_ID, PROJECT_ID from invoice_pay where INPUT_ID = #{INPUT_ID}
and IP_DATE >= #{PAY_START_TIME}
and IP_DATE <= #{PAY_END_TIME}

select '00' as IP_ID, '-' as IP_NUM, '合计' as IP_DATE, sum(IP_AMOUNT) as IP_AMOUNT, '-' as IP_REMARK, '-' as INPUT_ID, '-' as PROJECT_ID from invoice_pay where INPUT_ID = #{INPUT_ID}
and IP_DATE >= #{PAY_START_TIME}
and IP_DATE <= #{PAY_END_TIME}

mapper.update:
update invoice_pay set
	IP_DATE = #{IP_DATE},
	IP_AMOUNT = ${IP_AMOUNT},
	IP_REMARK = #{IP_REMARK}
where INPUT_ID = #{INPUT_ID}
