mapper.update:
update first_payment set 
PAYDATE = #{PAYDATE},
PAYMENT = ${PAYMENT},
BALANCE = ${BALANCE},
REMARK = #{REMARK}
where FIRST_ID = #{FIRST_ID}

update project_info 
set PROJECT_STATE = #{PROJECT_STATE}
where PROJECT_ID = #{PROJECT_ID}

mapper.select:
select MEMO_ID, MEMO_NUM, MEMO_TITLE, MEMO_CONTENT, CREATE_PERSON, CREATE_TIME, UPDATE_PERSON, UPDATE_TIME, MEMO_STATE, PROJECT_ID from project_memo where PROJECT_ID = #{PROJECT_ID}
and MEMO_STATE = #{MEMO_STATE}
order by CREATE_TIME asc

select COST_ID, COST_NUM, COST_DATE, COST_DETAIL, COST_AMOUNT, COST_TYPE, COST_REMARK, PROJECT_ID from project_cost where PROJECT_ID = #{PROJECT_ID}
and COST_DATE >= #{COST_START_TIME}
and COST_DATE <= #{COST_END_TIME}
and COST_TYPE = #{COST_TYPE}

select '00' as COST_ID, '-' as COST_NUM, '合计' as COST_DATE, '-' as COST_DETAIL, sum(COST_AMOUNT) as COST_AMOUNT, '-' as COST_TYPE, '-' as COST_REMARK, '-' as PROJECT_ID from project_cost where PROJECT_ID = #{PROJECT_ID}
and COST_DATE >= #{COST_START_TIME}
and COST_DATE <= #{COST_END_TIME}
and COST_TYPE = #{COST_TYPE}

select PAY_ID, PAY_NUM, PAY_DATE, PAY_AMOUNT, PAY_REMARK, PROJECT_ID from project_pay where PROJECT_ID = #{PROJECT_ID}
and PAY_DATE >= #{PAY_START_TIME}
and PAY_DATE <= #{PAY_END_TIME}

select '00' as PAY_ID, '-' as PAY_NUM, '合计' as PAY_DATE, sum(PAY_AMOUNT) as PAY_AMOUNT, '-' as PAY_REMARK, '-' as PROJECT_ID from project_pay where PROJECT_ID = #{PROJECT_ID}
and PAY_DATE >= #{PAY_START_TIME}
and PAY_DATE <= #{PAY_END_TIME}

mapper.update:
update project_pay set
	PAY_DATE = #{PAY_DATE},
	PAY_AMOUNT = ${PAY_AMOUNT},
	PAY_REMARK = #{PAY_REMARK}
where PAY_ID = #{PAY_ID}
